matrix = dlmread ("hw2_data1.txt", ",");VarName1 = matrix(:,1);VarName2 = matrix(:,2);VarName3 = matrix(:,3);A = [VarName1, VarName2, VarName3]; %Data%Part one graph ----------------------for i = 1:100    if(VarName3(i) == 0)        scatter(VarName1(i), VarName2(i), 'O');        hold on;    else        scatter(VarName1(i), VarName2(i), '+');        hold on;    endifendfor%---------------------------------------%Logistic Regression%-------Variables----------theta0 = 0;theta1 = 0;theta2 = 0;theta = [theta2; theta1; theta0];O = []; %Erroralpha = .005; %Learning ratetol = .000001; %--------------------%------Learn new Theta Values-----for i =1:1000 %Number of rounds    returnResult = logistic_function(A, theta);    for y=1:100 %Variables to loop through      temp0 = theta0 - alpha*sum(returnResult - VarName3); %Classifer      temp1 = theta1 - alpha*(sum(returnResult - VarName3).*VarName2(y));      temp2 = theta2 - alpha*(sum(returnResult - VarName3).*VarName1(y));    endfor    theta2 = temp2;    theta1 = temp1;    theta0 = temp0;        theta = [theta2;theta1;theta0];    returnResult = logistic_function(A, theta);    O(i) = -1/100 * (sum(VarName1.*log(returnResult)+(1-VarName1).*log(1-returnResult))); %Cost function    if (i > 1) && (abs(O(i)-O(i-1))<tol) %Break if change is below tol        break;    endifendfor%----------------------------%----Display Results-------figure(2)plot(O); % Bdisp(theta2, theta1);%----Logistic regression function-----% Input: 100x3 matrix(A) 3x1 matrix(Theta) % Output: 100x1 Matric(hTheta)function hTheta = logistic_function(A, theta)    thetaX = A*theta;    e = exp(-thetaX);    hTheta = 1./(1+e);endfunction%------------------------------