matrix = dlmread ("hw2_data1.txt", ",");#display(matrix);X = matrix(:, 1:2);x1 = X(:, 1);x2 = X(:, 2);#display(X);y = matrix(:, 3);#display(y);for i=1:100  if y(i) == 0    scatter(x1(i), x2(i), "o");    hold on;  else    scatter(x1(i), x2(i), "+");    hold on;  endifendfortheta0 = 100;theta1 = 10;theta2 = -26;theta = [theta2; theta1; theta0];m = size(X, 1);O = [];alpha = .0001;tol = .000001;function hthetax = logistic_sig(A, theta)  thetaX = A*theta;  e = exp(-thetaX);  hthetax = 1./(1+e);endfunctionX = matrix;#display(X);for i=1:1000  htheta = logistic_sig(X, theta);  #display(htheta);    temp0 = theta0 - alpha*sum(htheta-y);    temp1 = theta1 - alpha*sum((htheta-y).*X(:,2));    temp2 = theta2 - alpha*sum((htheta-y).*X(:,1));    theta2 = temp2;    theta1 = temp1;    theta0 = temp0;    theta = [theta2;theta1;theta0];  #display(theta);  htheta = logistic_sig(X, theta);  htheta(find(htheta==1))=.995;  O(i) = (-1/m)*sum(y.*log(htheta)+(1-y).*log(1-htheta));  #display(O(i));  if (i > 1) && (abs(O(i)-O(i-1))<tol)    break;  endifendfordisplay(i);display(theta);display(O);#htheta = logistic_sig(X, theta);ytest=(-1*theta(1)*X(:,1) - 1*theta(3))./theta(2);plot(x1, ytest);figure 2;plot(O);figure 3;Atest=[4 40 1; 1 8 1; 2 16 1];htheta_test=logistic_sig(Atest,theta);test_label=zeros(6,1);test_label=(htheta_test>=.5);